const prompt = require('prompt-sync')();

let num1 = Number(prompt('Insert Number 1: '));
let num2 = Number(prompt('Insert Number 2: '));




function calc(num1, num2, ){
    
    result = num1+num2
    console.log(`Number1 + Number2 (Addition) = ${result}`);

    result = num1-num2 
    console.log("Number1 - Number2 (Substraction) = " +result);

    result = num1*num2
    console.log("Number1 * Number2 (Multiplication) = " +result);

    result = num1/num2
    console.log("Number1 / Number2 (Division) = " +result);

    result = num1%num2
    console.log("Number1 % Number2 (Modulation) = " +result);
}

calc(num1, num2);

